#!/bin/bash
export KUBECONFIG=./kubeconfig
oc login --token=${p:environment/oc-token} --server=${p:environment/oc-url}
oc project ${p:environment/oc-project}

echo "I am user $(id)"
echo "My Home directory is $HOME"
echo "My current OpenShift project is $(oc project)"
echo "Pods running in this namespace/project are"
oc get pods

#!/bin/bash
export KUBECONFIG=./kubeconfig

oc delete route kubetoy-route --namespace=${p:environment/oc-project}

#!/bin/bash
export KUBECONFIG=./kubeconfig
ls -al
oc delete -f deployment/kubetoy-all-in-one.yaml --namespace=${p:environment/oc-project}

oc login --token=${p:environment/oc-token} --server=${p:environment/oc-url}
oc project ${p:environment/oc-project}
oc get pods

#!/bin/sh
sleep 5
export KUBECONFIG=./kubeconfig
oc login --token=${p:environment/oc-token} --server=${p:environment/oc-url}
oc project ${p:environment/oc-project}
oc expose service kubetoy-service -l name=kubetoy-route --name=kubetoy-route

